mapper.update:
UPDATE board
SET
	board_title = #{boardTitle},
	board_content = #{boardContent},
	board_pic = #{boardPic}
WHERE board_no = #{boardNo}

mapper.select:
SELECT board_pic
FROM board
WHERE board_no = #{boardNo}


	SELECT IFNULL(MAX(board_no), 0)
	FROM board
	WHERE board_no < #{boardNo}

SELECT IFNULL(MIN(board_no), 0)
FROM board
WHERE board_no > #{boardNo}
